CREATE TABLE `magazin`.`produse` (`idprodus` INT UNSIGNED NOT NULL AUTO_INCREMENT , `numeprodus` VARCHAR(350) NOT NULL , `cantitate` INT UNSIGNED NOT NULL , `idfirma` INT UNSIGNED NOT NULL , `firma` VARCHAR(250) NOT NULL , `adresafirma` VARCHAR(500) NOT NULL , `modelp` VARCHAR(500) NOT NULL , `stocp` INT NOT NULL , `pret` DECIMAL(8,2) UNSIGNED NOT NULL , `categorie` TINYINT NOT NULL , `descriereprodus` MEDIUMTEXT NOT NULL , PRIMARY KEY (`idprodus`)) ENGINE = InnoDB;
INSERT INTO `produse` (`idprodus`, `numeprodus`, `cantitate`, `idfirma`, `firma`, `adresafirma`, `modelp`, `stocp`, `pret`, `categorie`, `descriereprodus`) VALUES (NULL, 'TV', '150', '1', 'LG', 'Adresa LG', '123 SMART', '150', '22.5', '1', 'DESCRIERE PRODUS');

CREATE TABLE `facultate2`.`student` (`idstudent` INT UNSIGNED NOT NULL AUTO_INCREMENT , `nume` VARCHAR(200) NOT NULL , `prenume` VARCHAR(200) NOT NULL , `grupa` ENUM('1','2','3','4') NOT NULL , `email` VARCHAR(500) NOT NULL , `data_add` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `status` ENUM('admis','respins','neevaluat') NOT NULL DEFAULT 'neevaluat' , PRIMARY KEY (`idstudent`), UNIQUE `email` (`email`)) ENGINE = InnoDB;
